targets.uql:
parse-json
| jsonata '
(
  $q := ["0.10","0.50","0.90"];                      /* ✏️  pick the quantiles you need */
  (
    $map(
      forecasts[metric.__name__ = "cost_usd_per_cluster_forecast"
                and metric.quantile in $q],
      function($f){
        $map($f.timestamps, function($t,$i){
          {
            "_time":  $t,                            /* epoch-ms → datetime in UQL later */
            "USD":    $f.values[$i],                 /* numeric value                */
            "quantile": $f.metric.quantile           /* keep a label for colouring   */
          }
        })
      }
    )
  ).*
)'
| project
    _time     = unixtime_milliseconds_todatetime(_time),
    USD       = tonumber(USD),
    quantile

